// src/App.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

// The base URL of your backend API
const API_URL = 'http://localhost:5000/items';

function App() {
  const [items, setItems] = useState([]);
  const [newItemText, setNewItemText] = useState('');

  // NEW STATE for editing
  const [editingItemId, setEditingItemId] = useState(null);
  const [editText, setEditText] = useState('');

  // 1. Fetch items from the backend when the app loads
  useEffect(() => {
    axios.get(API_URL)
      .then(response => {
        setItems(response.data);
      })
      .catch(error => {
        console.error('There was an error fetching the items!', error);
      });
  }, []);

  // 2. Handle adding a new item
  const handleAddItem = (e) => {
    e.preventDefault();
    if (!newItemText.trim()) return;

    axios.post(`${API_URL}/add`, { text: newItemText })
      .then(response => {
        return axios.get(API_URL);
      })
      .then(response => {
        setItems(response.data);
        setNewItemText('');
      })
      .catch(error => {
        console.error('There was an error adding the item!', error);
      });
  };

  // 3. Handle deleting an item
  const handleDeleteItem = (id) => {
    axios.delete(`${API_URL}/${id}`)
      .then(response => {
        setItems(items.filter(item => item._id !== id));
      })
      .catch(error => {
        console.error('There was an error deleting the item!', error);
      });
  };

  // 4. NEW: Handle clicking the "Edit" button
  const handleEditClick = (item) => {
    setEditingItemId(item._id);
    setEditText(item.text);
  };

  // 5. NEW: Handle updating an item
  const handleUpdateItem = (id) => {
    if (!editText.trim()) return;

    axios.put(`${API_URL}/update/${id}`, { text: editText })
      .then(response => {
        // Update the item in the local state
        const updatedItems = items.map(item => {
          if (item._id === id) {
            return { ...item, text: editText };
          }
          return item;
        });
        setItems(updatedItems);

        // Exit editing mode
        setEditingItemId(null);
        setEditText('');
      })
      .catch(error => {
        console.error('There was an error updating the item!', error);
      });
  };


  return (
    <div className="app-container">
      <div className="todo-list">
        <h1>My To-Do List</h1>
        <form className="add-item-form" onSubmit={handleAddItem}>
          <input
            type="text"
            placeholder="Add a new item..."
            value={newItemText}
            onChange={(e) => setNewItemText(e.target.value)}
          />
          <button type="submit">Add</button>
        </form>
        <ul>
          {items.map(item => (
            <li key={item._id}>
              {editingItemId === item._id ? (
                // If this is the item being edited, show an input field
                <>
                  <input
                    type="text"
                    className="edit-input"
                    value={editText}
                    onChange={(e) => setEditText(e.target.value)}
                  />
                  <button className="save-btn" onClick={() => handleUpdateItem(item._id)}>Save</button>
                </>
              ) : (
                // Otherwise, show the normal text and buttons
                <>
                  <span>{item.text}</span>
                  <div className="item-buttons">
                    <button className="edit-btn" onClick={() => handleEditClick(item)}>Edit</button>
                    <button className="delete-btn" onClick={() => handleDeleteItem(item._id)}>Delete</button>
                  </div>
                </>
              )}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
