// src/App.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

// The base URL of your backend API
const API_URL = 'http://localhost:5000/items';

function App() {
  const [items, setItems] = useState([]);
  const [newItemText, setNewItemText] = useState('');

  // 1. Fetch items from the backend when the app loads
  useEffect(() => {
    axios.get(API_URL)
      .then(response => {
        setItems(response.data);
      })
      .catch(error => {
        console.error('There was an error fetching the items!', error);
      });
  }, []); // The empty array [] means this effect runs only once

  // 2. Handle adding a new item
  const handleAddItem = (e) => {
    e.preventDefault(); // Prevent the form from reloading the page
    if (!newItemText.trim()) return; // Don't add empty items

    axios.post(`${API_URL}/add`, { text: newItemText })
      .then(response => {
        // To see the new item immediately, we can refetch or just add it to the state
        // Let's refetch for simplicity and to get the ID from the database
        return axios.get(API_URL);
      })
      .then(response => {
        setItems(response.data);
        setNewItemText(''); // Clear the input box
      })
      .catch(error => {
        console.error('There was an error adding the item!', error);
      });
  };

  // 3. Handle deleting an item
  const handleDeleteItem = (id) => {
    axios.delete(`${API_URL}/${id}`)
      .then(response => {
        // Remove the item from the local state to update the UI
        setItems(items.filter(item => item._id !== id));
      })
      .catch(error => {
        console.error('There was an error deleting the item!', error);
      });
  };

  return (
    <div className="app-container">
      <div className="todo-list">
        <h1>My To-Do List</h1>
        <form className="add-item-form" onSubmit={handleAddItem}>
          <input
            type="text"
            placeholder="Add a new item..."
            value={newItemText}
            onChange={(e) => setNewItemText(e.target.value)}
          />
          <button type="submit">Add</button>
        </form>
        <ul>
          {items.map(item => (
            <li key={item._id}>
              <span>{item.text}</span>
              <button className="delete-btn" onClick={() => handleDeleteItem(item._id)}>
                Delete
              </button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
